   		and datetime <=#{jsjy_date,jdbcType=TIMESTAMP}
   		
        	and lx=#{lx,jdbcType=INTEGER}

	    select 
	    
	    from pay_moneybd
	   	where UserID = #{userId,jdbcType=INTEGER} 
	    and datetime >=#{ksjy_date,jdbcType=TIMESTAMP} 
	   	and datetime <=#{jsjy_date,jdbcType=TIMESTAMP}
	   	
        	and lx=#{lx,jdbcType=INTEGER}
   		
	   	order by datetime desc

